import Vue from 'vue';
import App from './App.vue';
import VueRouter from "vue-router";


Vue.use(VueRouter); // Corrected the method name

const routes = [
    {
        path: '/',
        name: 'home',
        component: Home
    },
    {
        path: '/about',
        name: 'about',
        component: About
    },
];

const router = new VueRouter({
    mode: "history",
    routes,
});

new Vue({
    router,
    render: (h) => h(App),
}).$mount('#app');
